import { Suspense } from 'react';
import { useLoaderData, json, defer, Await } from 'react-router-dom';

import EventsList from '../components/EventsList';

const EventsPage = () => {
    // Option-1(Without using defer) :-
    // const data = useLoaderData();

    // // if (data.isError) {
    // //     return <p>{data.message}</p>
    // // }
    // const events = data.events;

    // return <EventsList events={events} />

    // Option-2(Using defer) :-
    const { events } = useLoaderData();

    return (
        <Suspense fallback={<p style={{textAlign: "center"}}>Loading...</p>}>                  {/* The Suspense component from React is used to show the fallback while we are waiting for other data to arrive. */}                 
            <Await resolve={events}>
                {(loadEvents) => <EventsList events={loadEvents} />}
            </Await>
        </Suspense>
    );
}

export default EventsPage;

async function loadEvents() {
    const response = await fetch('http://localhost:8080/events');

    if (!response.ok) {
        // return {isError: true, message: "Could not fetch events."};
        // OR :-
        // throw {message: "Could not fetch events."};
        // OR :-
        // throw new Response(JSON.stringify({ message: "Could not fetch events!" }), { status: 500 });
        // OR :-
        throw json({ message: "Could not fetch events!" }, { status: 500 });    // json from react-router-dom.
    } else {
        // const resData = await response.json();
        // return resData.events;
        const resData = await response.json();
        return resData;
    }
}

export function loader() {
    return defer({
        events: loadEvents()
    });
}
