import { useNavigate, Form, useNavigation, useActionData, json, redirect } from 'react-router-dom';

import classes from './EventForm.module.css';

function EventForm({ method, event }) {
  const data = useActionData();          // This hook returns data of action, just like useLoaderData() hook returns data of loader.
  const navigate = useNavigate();
  const navigation = useNavigation();

  const isSubmitting = navigation.state === "submitting";

  function cancelHandler() {
    navigate('..');
  }
  // Form from react-router-dom takes the request(data submited by the form) and gives it to the action function.
  return (
    // <Form method="post" className={classes.form}>
    <Form method={method} className={classes.form}>
      {data && data.errors && <ul>
        {Object.values(data.errors).map(err => <li key={err}>{err}</li>)}       {/* Object.values() is a javascript function that loops over all the keys in the object. */}
      </ul>}
      <p>
        <label htmlFor="title">Title</label>
        <input
          id="title"
          type="text"
          name="title"
          required
          defaultValue={event ? event.title : ""}
        />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input
          id="image"
          type="url"
          name="image"
          required
          defaultValue={event ? event.image : ""}
        />
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input
          id="date"
          type="date"
          name="date"
          required
          defaultValue={event ? event.date : ""}
        />
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea
          id="description"
          name="description"
          rows="5"
          required
          defaultValue={event ? event.description : ""}
        />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler} disabled={isSubmitting}>
          Cancel
        </button>
        <button disabled={isSubmitting}>{isSubmitting ? "Submitting" : "Save"}</button>
      </div>
    </Form>
  );
}

export default EventForm;

export async function action({request, params}) {
  const method = request.method;
  const data = await request.formData();                       // formData() is an inbuilt function of action.

  const eventData = {
      title: data.get("title"),                                // Extracting data using in-built function get() and using name fo the
      image: data.get("image"),                                // inputs in the Form.
      date: data.get("date"),
      description: data.get("description"),
  }

  let url = "http://localhost:8000/events";

  if (method === "PATCH" ) {
    const eventId = params.eventId;
    url = "http://localhost:8000/events/" + eventId;
  }

  const response = await fetch(url , {
      method: method,
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify(eventData)
  });

  if (response.status === 422) {
      return response;
  }

  if (!response.ok) {
      throw json({message: "could not save event"}, {status: 500});
  }

  return redirect("/events");
}
